#tle
from collections import defaultdict
class Solution:
    def threeSumMulti(self, arr: [int], target: int) -> int:
        modulo = 10**9+7
        d = defaultdict(set)
        for i, num in enumerate(arr):
            d[target-num].add(i)
        c = 0
        for i in range(len(arr)):
            for j in range(i+1, len(arr)):
                key = arr[i] + arr[j]
                if key in d:
                    for k in d[key]:
                        if k > j:
                            c += 1
        return c%modulo


#two pointers O(n*n)
class Solution:
    def threeSumMulti(self, arr: List[int], target: int) -> int:
        mod = 10**9+7
        d = Counter(arr)
        keys = sorted(d)
        ans = 0
        
        for i, x in enumerate(keys):
            T = target - x
            j, k = i, len(keys)-1
            while j <= k:
                y, z = keys[j], keys[k]
                if y + z < T:
                    j += 1
                elif y + z > T:
                    k -= 1
                else:
                    if x != y != z:
                        ans += d[x]*d[y]*d[z]
                    elif y == z != x:
                        ans += d[y]*(d[y]-1)*d[x]//2
                    elif x == y != z:
                        ans += d[x]*(d[x]-1)*d[z]//2
                    else:
                        ans += d[x]*(d[x]-1)*(d[x]-2)//6
                    j += 1
                    k -= 1

        return ans % mod
